INSERT INTO `csl` VALUES (2,'APA','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<style xmlns=\"http://purl.org/net/xbiblio/csl\" class=\"in-text\" version=\"1.0\" demote-non-dropping-particle=\"never\">\n  <info>\n	<title>American Psychological Association 6th Edition</title>\n	<id>http://www.zotero.org/styles/apa</id>\n	<link href=\"http://www.zotero.org/styles/apa\" rel=\"self\"/>\n	<link href=\"http://owl.english.purdue.edu/owl/resource/560/01/\" rel=\"documentation\"/>\n	<author>\n	  <name>Simon Kornblith</name>\n	  <email>simon@simonster.com</email>\n	</author>\n	<contributor>\n	  <name>Bruce D\'Arcus</name>\n	</contributor>\n	<contributor>\n	  <name>Curtis M. Humphrey</name>\n	</contributor>\n	<contributor>\n	  <name>Richard Karnesky</name>\n	  <email>karnesky+zotero@gmail.com</email>\n	  <uri>http://arc.nucapt.northwestern.edu/Richard_Karnesky</uri>\n	</contributor>\n	<contributor>\n	  <name>Sebastian Karcher</name>\n	</contributor>\n	<category field=\"psychology\"/>\n	<category field=\"generic-base\"/>\n	<category citation-format=\"author-date\"/>\n	<updated>2011-08-23T19:22:34+00:00</updated>\n	<rights>This work is licensed under a Creative Commons Attribution-Share Alike 3.0 License: http://creativecommons.org/licenses/by-sa/3.0/</rights>\n  </info>\n  <locale xml:lang=\"en\">\n	<terms>\n	  <term name=\"translator\" form=\"short\">\n		<single>trans.</single>\n		<multiple>trans.</multiple>\n	  </term>\n	</terms>\n  </locale>\n  <macro name=\"container-contributors\">\n	<choose>\n	  <if type=\"chapter paper-conference\" match=\"any\">\n		<text term=\"in\" text-case=\"capitalize-first\" suffix=\" \"/>\n		<names variable=\"editor\" delimiter=\", \" suffix=\", \">\n		  <name and=\"symbol\" initialize-with=\". \" delimiter=\", \"/>\n		  <label form=\"short\" prefix=\" (\" text-case=\"capitalize-first\" suffix=\")\"/>\n		  <substitute>\n			<names variable=\"translator\"/>\n		  </substitute>\n		</names>\n	  </if>\n	</choose>\n  </macro>\n  <macro name=\"secondary-contributors\">\n	<choose>\n	  <if type=\"chapter paper-conference\" match=\"none\">\n		<names variable=\"translator\" delimiter=\", \" prefix=\" (\" suffix=\")\">\n		  <name and=\"symbol\" initialize-with=\". \" delimiter=\", \"/>\n		  <label form=\"short\" prefix=\", \" text-case=\"capitalize-first\" suffix=\"\"/>\n		  <substitute>\n			<names variable=\"editor\"/>\n		  </substitute>            \n		</names>\n	  </if>\n	</choose>\n  </macro>\n  <macro name=\"author\">\n	<names variable=\"author\">\n	  <name name-as-sort-order=\"all\" and=\"symbol\" sort-separator=\", \" initialize-with=\". \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n	  <label form=\"short\" prefix=\" (\" suffix=\".)\" text-case=\"capitalize-first\" strip-periods=\"true\"/>\n	  <substitute>\n		<names variable=\"editor\"/>\n		<names variable=\"translator\"/>\n		<choose>\n		  <if type=\"report\">\n			<text variable=\"publisher\"/>\n			<text macro=\"title\"/>\n		  </if>\n		  <else>\n			<text macro=\"title\"/>\n		  </else>\n		</choose>\n	  </substitute>\n	</names>\n  </macro>\n  <macro name=\"author-short\">\n	<names variable=\"author\">\n	  <name form=\"short\" and=\"symbol\" delimiter=\", \" initialize-with=\". \"/>\n	  <substitute>\n		<names variable=\"editor\"/>\n		<names variable=\"translator\"/>\n		<choose>\n		  <if type=\"report\">\n			<text variable=\"publisher\"/>\n			<text variable=\"title\" form=\"short\" font-style=\"italic\"/>\n		  </if>\n		  <else-if type=\"bill book graphic legal_case motion_picture song\" match=\"any\">\n			<text variable=\"title\" form=\"short\" font-style=\"italic\"/>\n		  </else-if>\n		  <else>\n			<text variable=\"title\" form=\"short\" quotes=\"true\"/>\n		  </else>\n		</choose>\n	  </substitute>\n	</names>\n  </macro>\n  <macro name=\"access\">\n	<choose>\n	  <if type=\"thesis\">\n		<choose>\n		  <if variable=\"archive\" match=\"any\">\n			<group>\n			  <text term=\"retrieved\" text-case=\"capitalize-first\" suffix=\" \"/>\n			  <text term=\"from\" suffix=\" \"/>\n			  <text variable=\"archive\" suffix=\".\"/>\n			  <text variable=\"archive_location\" prefix=\" (\" suffix=\")\"/>\n			</group>\n		  </if>\n		  <else>\n			<group>\n			  <text term=\"retrieved\" text-case=\"capitalize-first\" suffix=\" \"/>\n			  <text term=\"from\" suffix=\" \"/>\n			  <text variable=\"URL\"/>\n			</group>\n		  </else>\n		</choose>\n	  </if>\n	  <else>\n		<choose>\n		  <if variable=\"DOI\">\n			<text variable=\"DOI\" prefix=\"doi:\"/>\n		  </if>\n		  <else>\n			<choose>\n			  <if type=\"webpage\">\n				<group delimiter=\" \">\n				  <text term=\"retrieved\" text-case=\"capitalize-first\" suffix=\" \"/>\n				  <group>\n					<date variable=\"accessed\" suffix=\", \">\n					  <date-part name=\"month\" suffix=\" \"/>\n					  <date-part name=\"day\" suffix=\", \"/>\n					  <date-part name=\"year\"/>\n					</date>\n					<text variable=\"year-suffix\"/>\n				  </group>\n				  <text term=\"from\"/>\n				  <text variable=\"URL\"/>\n				</group>\n			  </if>\n			  <else>\n				<group>\n				  <text term=\"retrieved\" text-case=\"capitalize-first\" suffix=\" \"/>\n				  <text term=\"from\" suffix=\" \"/>\n				  <text variable=\"URL\"/>\n				</group>\n			  </else>\n			</choose>\n		  </else>\n		</choose>\n	  </else>\n	</choose>\n  </macro>\n  <macro name=\"title\">\n	<choose>\n	  <if type=\"report thesis\" match=\"any\">\n		<text variable=\"title\" font-style=\"italic\"/>\n		<group prefix=\" (\" suffix=\")\">\n		  <text variable=\"genre\"/>\n		  <text variable=\"number\" prefix=\" No. \"/>\n		</group>\n	  </if>\n	  <else-if type=\"bill book graphic legal_case motion_picture report song manuscript speech\" match=\"any\">\n		<text variable=\"title\" font-style=\"italic\"/>\n	  </else-if>\n	  <else>\n		<text variable=\"title\"/>\n	  </else>\n	</choose>\n  </macro>\n  <macro name=\"publisher\">\n	<choose>\n	  <if type=\"report\" match=\"any\">\n		<group delimiter=\": \">\n		  <text variable=\"publisher-place\"/>\n		  <text variable=\"publisher\"/>\n		</g